package com.abstratt.kirra;

/**
 * Represents a property.
 */
public class Property extends DataElement {

    private static final long serialVersionUID = 1;

    protected boolean unique;
    protected boolean userVisible;
    protected Object defaultValue;

    public Property() {
    }

    /**
     * An auto-generated property has its value automatically assigned.
     */
    public boolean isAutoGenerated() {
        return isUnique() && !isInitializable() && !isEditable();
    }

    /**
     * A unique property does not allow duplicated values.
     */
    public boolean isUnique() {
        return unique;
    }

    public void setUnique(boolean unique) {
        this.unique = unique;
    }
    
    public Object getDefaultValue() {
        return defaultValue;
    }
    
    public void setDefaultValue(Object value) {
        this.defaultValue = value;
    }
    
    public boolean isUserVisible() {
        return userVisible;
    }
    public void setUserVisible(boolean userVisible) {
        this.userVisible = userVisible;
    }
}